import React, { useState } from "react";
import DriveVideoEmbed from "./DriveVideoEmbed.jsx";
import { useMediaQuery } from "react-responsive";

function VideoSlider({ videoList }) {
    const isDesktop = useMediaQuery({ query: "(min-width: 801px)" });
    const visibleVideos = isDesktop ? 4 : 2;

    const [currentPosition, setCurrentPosition] = useState(0);

    const totalVideos = videoList.length;
    const maxPosition = Math.max(0, totalVideos - visibleVideos);

    const handleNext = () => {
        setCurrentPosition((prev) => (prev + visibleVideos > maxPosition ? 0 : prev + visibleVideos));
    };

    const handlePrev = () => {
        setCurrentPosition((prev) => (prev - visibleVideos < 0 ? maxPosition : prev - visibleVideos));
    };

    const displayedVideos = [
        ...videoList.slice(currentPosition, currentPosition + visibleVideos),
        ...(currentPosition + visibleVideos > totalVideos ? videoList.slice(0, (currentPosition + visibleVideos) % totalVideos) : []),
    ];

    return (
        <div className="arrows-videos-container">
            {totalVideos > visibleVideos && (
                <button
                    className="arrows"
                    onClick={handlePrev}
                    style={{
                        opacity: currentPosition === 0 ? 0.5 : 1,
                        cursor: currentPosition === 0 ? "not-allowed" : "pointer",
                    }}
                >
                    &#10094;
                </button>
            )}

            <div className="videos-container">
                <div
                    className="videos-slider"
                    style={{
                        display: "flex",
                        gap: "20px",
                        overflow: "hidden",
                    }}
                >
                    {displayedVideos.map((video, index) => (
                        <div key={index} className="video-item">
                            <DriveVideoEmbed
                                videoUrl={video.videoUrl}
                                vertical={video.vertical}
                                title={video.title}
                                subtitle={video.subtitle}
                            />
                        </div>
                    ))}
                </div>
            </div>

            {totalVideos > visibleVideos && (
                <button
                    className="arrows"
                    onClick={handleNext}
                    style={{
                        opacity: currentPosition === maxPosition ? 0.5 : 1,
                        cursor: currentPosition === maxPosition ? "not-allowed" : "pointer",
                    }}
                >
                    &#10095;
                </button>
            )}
        </div>
    );
}

export default VideoSlider;
