import React, { useState } from "react";
import DriveVideoEmbed from "./DriveVideoEmbed.jsx";
import { useMediaQuery } from "react-responsive";

function VideoSlider({ videoList }) {
    const isDesktop = useMediaQuery({ query: "(min-width: 801px)" });
    const visibleVideos = isDesktop ? 4 : 2;
    const [initialId, setInitialId] = useState(0);
    const [displayVideos, setDisplayVideos] = useState(videoList.slice(0, visibleVideos));

    const handleNext = () => {
        setInitialId((initialId + visibleVideos) % videoList.length);
        setDisplayVideos(videoList.slice(initialId, initialId + visibleVideos));
    }

    const handlePrev = () => {
        setInitialId((initialId - visibleVideos + videoList.length) % videoList.length);
        const diff = videoList.length - initialId;
        const remaining = visibleVideos - diff;
        console.log("id: " + initialId + " diff: " + diff + " remaining: " + remaining);
        let tempVideos = videoList.slice(initialId, initialId + diff);

        setDisplayVideos(videoList.slice(initialId, initialId + visibleVideos));
    }

    return (
        <div className="arrows-videos-container">
            {videoList.length > visibleVideos &&
                <button className="arrows" onClick={handlePrev}>&#10094;</button>
            }

            {displayVideos.map((video) => (
                <div
                    key={video.id}
                    className="video-item"
                >
                    <p>{video.id}</p>
                    <DriveVideoEmbed
                        videoUrl={video.videoUrl}
                        vertical={video.vertical}
                        title={video.title}
                        subtitle={video.subtitle}
                    />
                </div>
            ))}

            {videoList.length > visibleVideos &&
                <button className="arrows" onClick={handleNext}>&#10095;</button>
            }

        </div>
    );
}

export default VideoSlider;
