import React, { useState } from "react";
// @ts-ignore
import './css/navbar.css';

function Nabvar({ categories }) {
    const [isOpen, setIsOpen] = useState(false); // For toggling dropdown visibility
    const [selectedCategory, setSelectedCategory] = useState("All"); // Track selected category

    const toggleIcon = () => {
        setIsOpen(!isOpen);
    };

    const handleCategoryChange = (event) => {
        setSelectedCategory(event.target.value); // Update selected category
        console.log("Selected category:", event.target.value);
    };

    return (
        <div className="nav-bar">
            <div className="nav-bar-left">
                <h1>Video Gallery</h1>
                <div className="dropdown">
                    <button id="category" onClick={toggleIcon}>
                        Category
                        <i className={`fa ${isOpen ? "fa-caret-left" : "fa-caret-down"}`} />
                    </button>

                    {isOpen && ( // Only show dropdown when `isOpen` is true
                        <div className="dropdown-content">
                            <div className="radio-buttons">
                                {categories.map((category, index) => (
                                    <label key={index}>
                                        <input
                                            type="radio"
                                            name="category"
                                            value={category}
                                            checked={selectedCategory === category}
                                            onChange={handleCategoryChange}
                                        />
                                        {category}
                                    </label>
                                ))}
                            </div>
                        </div>
                    )}
                </div>
            </div>

            <div className="nav-bar-right">
                {/* Add any additional content for the right side of the navbar */}
            </div>
        </div>
    );
}

export default Nabvar;
