import React, { useEffect } from "react";
function DriveVideoEmbed({ videoUrl, id, playing, onPlay, width, height}) {
	useEffect(() => {
        if (playing !== id) {
            const iframe = document.getElementById(`video-${id}`);
            if (iframe) {
                iframe.contentWindow.postMessage('{"event":"command","func":"pauseVideo","args":""}', '*');
            }
        }
    }, [playing, id]);

    return (
        <div>
            <iframe
			    id={`video-${id}`}
                src={`${videoUrl}?autoplay=${playing ? 1 : 0}`}
                style={{
                    top: 0,
                    left: 0,
                    width: width,
                    height: height,
                    border: "none",
                }}
                allow="autoplay"
                title="Google Drive Video"
                onPlay={onPlay} // Custom handler for play events
            ></iframe>
        </div>
    );
}

export default DriveVideoEmbed;
