const DriveVideoEmbed = forwardRef(({ videoUrl, vertical, title, subtitle, videoId, isPlaying, onPlay, thumbnail }, ref) => {
    const isDesktop = useMediaQuery({ query: "(min-width: 801px)" });

    let width, height;
    if (vertical) {
        width = isDesktop ? "15vw" : "30vw";
        height = `${(parseFloat(width) * 16) / 9}vw`;
    } else {
        height = "15vw";
        width = "26.6666666666vw";
    }

    useEffect(() => {
        if (ref?.current && isPlaying) {
            ref.current.contentWindow.postMessage(
                '{"event":"command","func":"playVideo","args":""}',
                "*"
            );
        } else if (ref?.current && !isPlaying) {
            ref.current.contentWindow.postMessage(
                '{"event":"command","func":"pauseVideo","args":""}',
                "*"
            );
        }
    }, [isPlaying, ref]);

    const embedUrl = `${videoUrl}?enablejsapi=1&autoplay=${isPlaying ? 1 : 0}&mute=1`;

    return (
        <div className="video-container">
            {!isPlaying ? (
                <div
                    className="thumbnail-overlay"
                    style={{
                        width,
                        height,
                        position: "relative",
                        cursor: "pointer",
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        backgroundColor: thumbnail ? "transparent" : "#292929",
                        backgroundImage: thumbnail ? `url(${thumbnail})` : "none",
                        backgroundSize: "cover",
                        backgroundPosition: "center",
                        borderRadius: "5px",
                    }}
                    onClick={() => onPlay(videoId)}
                ></div>
            ) : (
                <iframe
                    ref={ref}
                    id={`iframe-${videoId}`}
                    src={embedUrl}
                    style={{
                        top: 0,
                        left: 0,
                        width,
                        height,
                        border: "none",
                        borderRadius: "5px",
                    }}
                    allow="autoplay; encrypted-media"
                    allowFullScreen
                    title={title}
                ></iframe>
            )}
        </div>
    );
});
