import React, { useState } from "react";
import DriveVideoEmbed from "./DriveVideoEmbed.jsx";

function VideoSlider({ videoList }) {
    const [currentPosition, setCurrentPosition] = useState(0);
    const visibleVideos = 3; // Nombre de vídeos visibles a la pantalla
    const maxPosition = Math.max(0, videoList.length - visibleVideos); // Màxima posició

    const handleNext = () => {
        if (currentPosition < maxPosition) {
            setCurrentPosition(currentPosition + 1);
        }
    };

    const handlePrev = () => {
        if (currentPosition > 0) {
            setCurrentPosition(currentPosition - 1);
        }
    };

    return (
        <div className="arrows-videos-container">
            <button className="arrows" onClick={handlePrev}>&#10094;</button>
            <div className="videos-container">
                <div
                    className="videos-slider"
                    style={{
                        transform: `translateX(-${currentPosition * 16}%)`,
                        transition: "transform 0.3s ease-in-out",
                        display: "flex",
                        gap: "20px",
                    }}
                >
                    {videoList.map((video, index) => (
                        <DriveVideoEmbed
                            key={index}
                            videoUrl={video.videoUrl}
                            vertical={video.vertical}
                        />
                    ))}
                </div>
            </div>
            <button className="arrows" onClick={handleNext}>&#10095;</button>
        </div>
    );
}

export default VideoSlider;
