import React, { useState, useEffect, useRef } from "react";
import { createRoot } from "react-dom/client";
import { BrowserRouter } from "react-router-dom";
import VideoSlider from "./VideoSlider.jsx";
// @ts-ignore
import './css/root.css';
import Footer from "./Footer.jsx";
import Nabvar from "./Navbar.jsx";

function Root() {
    const viewRef = useRef(null);
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState("All");
    const [searchQuery, setSearchQuery] = useState("");
    const [currentVideoId, setCurrentVideoId] = useState(null); // Global state for currently playing video

    let travelVideos = [
        // Example video list with unique IDs
        { videoUrl: "...", vertical: true, title: "Wisconsin", subtitle: "Summer '24", id: 1 },
        { videoUrl: "...", vertical: true, title: "Wisconsin", subtitle: "Summer '24", id: 2 },
    ];

    let otherCategoryVideos = [
        { videoUrl: "...", vertical: true, title: "Category 2", subtitle: "Video 1", id: 3 },
        { videoUrl: "...", vertical: true, title: "Category 2", subtitle: "Video 2", id: 4 },
    ];

    useEffect(() => {
        if (viewRef.current) {
            const h2Elements = viewRef.current.querySelectorAll("h2");
            const h2Text = Array.from(h2Elements).map((h2) => h2.textContent);
            setCategories(["All", ...h2Text]);
        }
    }, []);

    const filterVideos = (videos) => {
        if (!searchQuery) return videos;
        return videos.filter(
            (video) =>
                video.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                video.subtitle.toLowerCase().includes(searchQuery.toLowerCase())
        );
    };

    return (
        <BrowserRouter>
            <Nabvar
                categories={categories}
                selectedCategory={selectedCategory}
                setSelectedCategory={setSelectedCategory}
                setSearchQuery={setSearchQuery}
            />

            <div id="view" ref={viewRef}>
                {(selectedCategory === "All" || selectedCategory === "Travel") && (
                    <div>
                        <h2>Travel</h2>
                        <VideoSlider
                            videoList={filterVideos(travelVideos)}
                            currentVideoId={currentVideoId}
                            setCurrentVideoId={setCurrentVideoId}
                        />
                    </div>
                )}
                {(selectedCategory === "All" || selectedCategory === "Other") && (
                    <div>
                        <h2>Other</h2>
                        <VideoSlider
                            videoList={filterVideos(otherCategoryVideos)}
                            currentVideoId={currentVideoId}
                            setCurrentVideoId={setCurrentVideoId}
                        />
                    </div>
                )}
            </div>
            <div id="footer">
                <Footer />
            </div>
        </BrowserRouter>
    );
}

const rootElement = document.getElementById("root");
if (rootElement) {
    createRoot(rootElement).render(<Root />);
}
